import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Badge,
  Snackbar,
  Alert,
} from '@mui/material';
import {
  ShoppingCart as ShoppingCartIcon,
  Add as AddIcon,
  Remove as RemoveIcon,
  Delete as DeleteIcon,
} from '@mui/icons-material';

// Sample product data
const products = [
  {
    id: 1,
    name: 'Sülearvuti Pro X',
    price: 999.99,
    description: 'Võimas sülearvuti professionaalidele',
    image: 'https://picsum.photos/300/200?random=1',
    stock: 10
  },
  {
    id: 2,
    name: 'Nutitelefon Y20',
    price: 599.99,
    description: 'Tipptasemel nutitelefon suurepärase kaameraga',
    image: 'https://picsum.photos/300/200?random=2',
    stock: 15
  },
  {
    id: 3,
    name: 'Juhtmevabad Kõrvaklapid',
    price: 149.99,
    description: 'Kvaliteetne heli ja mürasummutus',
    image: 'https://picsum.photos/300/200?random=3',
    stock: 20
  },
  {
    id: 4,
    name: 'Nutikas Käekell',
    price: 199.99,
    description: 'Tervise ja aktiivsuse jälgimiseks',
    image: 'https://picsum.photos/300/200?random=4',
    stock: 8
  },
  {
    id: 5,
    name: 'Tahvelarvuti Air',
    price: 449.99,
    description: 'Õhuke ja kerge tahvelarvuti',
    image: 'https://picsum.photos/300/200?random=5',
    stock: 12
  },
  {
    id: 6,
    name: 'Mänguri Hiir',
    price: 79.99,
    description: 'Täpne ja ergonoomiline mänguhiir',
    image: 'https://picsum.photos/300/200?random=6',
    stock: 25
  },
  {
    id: 7,
    name: 'Mehaaniline Klaviatuur',
    price: 129.99,
    description: 'RGB valgustusega mänguri klaviatuur',
    image: 'https://picsum.photos/300/200?random=7',
    stock: 15
  },
  {
    id: 8,
    name: 'Veekindel Kõlar',
    price: 89.99,
    description: 'Kaasaskantav Bluetooth kõlar',
    image: 'https://picsum.photos/300/200?random=8',
    stock: 18
  },
  {
    id: 9,
    name: 'Kaamera Statiiv',
    price: 59.99,
    description: 'Professionaalne fotograafia statiiv',
    image: 'https://picsum.photos/300/200?random=9',
    stock: 22
  }
];

function ShopPage() {
  const [cart, setCart] = useState([]);
  const [cartOpen, setCartOpen] = useState(false);
  const [checkoutOpen, setCheckoutOpen] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: ''
  });

  const handleAddToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    setSnackbar({
      open: true,
      message: 'Toode lisatud ostukorvi',
      severity: 'success'
    });
  };

  const handleUpdateQuantity = (productId, change) => {
    setCart(cart.map(item => {
      if (item.id === productId) {
        const newQuantity = item.quantity + change;
        return newQuantity > 0 ? { ...item, quantity: newQuantity } : item;
      }
      return item;
    }));
  };

  const handleRemoveFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
    setSnackbar({
      open: true,
      message: 'Toode eemaldatud ostukorvist',
      severity: 'info'
    });
  };

  const handleCheckout = () => {
    // Simulate payment process
    const success = Math.random() > 0.5; // 50% chance of success
    if (success) {
      setCart([]);
      setSnackbar({
        open: true,
        message: 'Makse õnnestus! Täname ostu eest.',
        severity: 'success'
      });
    } else {
      setSnackbar({
        open: true,
        message: 'Makse ebaõnnestus. Palun proovige uuesti.',
        severity: 'error'
      });
    }
    setCheckoutOpen(false);
  };

  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
  const totalPrice = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          E-pood
        </Typography>
        <IconButton 
          color="primary" 
          onClick={() => setCartOpen(true)}
          sx={{ position: 'relative' }}
        >
          <Badge badgeContent={totalItems} color="error">
            <ShoppingCartIcon />
          </Badge>
        </IconButton>
      </Box>

      {/* Products Grid */}
      <Grid container spacing={3}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={product.image}
                alt={product.name}
              />
              <CardContent>
                <Typography gutterBottom variant="h6" component="div">
                  {product.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {product.description}
                </Typography>
                <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                  €{product.price.toFixed(2)}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Laos: {product.stock}
                </Typography>
              </CardContent>
              <CardActions>
                <Button 
                  size="small" 
                  variant="contained" 
                  onClick={() => handleAddToCart(product)}
                  fullWidth
                >
                  Lisa ostukorvi
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Shopping Cart Dialog */}
      <Dialog 
        open={cartOpen} 
        onClose={() => setCartOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Ostukorv</DialogTitle>
        <DialogContent>
          {cart.length === 0 ? (
            <Typography>Ostukorv on tühi</Typography>
          ) : (
            <Box>
              {cart.map((item) => (
                <Box 
                  key={item.id} 
                  sx={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    mb: 2,
                    p: 2,
                    border: '1px solid #eee',
                    borderRadius: 1
                  }}
                >
                  <Box sx={{ flexGrow: 1 }}>
                    <Typography variant="subtitle1">{item.name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      €{item.price.toFixed(2)} x {item.quantity}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <IconButton 
                      size="small"
                      onClick={() => handleUpdateQuantity(item.id, -1)}
                    >
                      <RemoveIcon />
                    </IconButton>
                    <Typography>{item.quantity}</Typography>
                    <IconButton 
                      size="small"
                      onClick={() => handleUpdateQuantity(item.id, 1)}
                    >
                      <AddIcon />
                    </IconButton>
                    <IconButton 
                      color="error"
                      onClick={() => handleRemoveFromCart(item.id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Box>
                </Box>
              ))}
              <Typography variant="h6" sx={{ mt: 2 }}>
                Kokku: €{totalPrice.toFixed(2)}
              </Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCartOpen(false)}>Sulge</Button>
          {cart.length > 0 && (
            <Button 
              variant="contained" 
              onClick={() => {
                setCartOpen(false);
                setCheckoutOpen(true);
              }}
            >
              Maksa
            </Button>
          )}
        </DialogActions>
      </Dialog>

      {/* Checkout Dialog */}
      <Dialog 
        open={checkoutOpen} 
        onClose={() => setCheckoutOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Maksmine</DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Eesnimi"
                fullWidth
                value={formData.firstName}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Perekonnanimi"
                fullWidth
                value={formData.lastName}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Email"
                fullWidth
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Telefon"
                fullWidth
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                required
              />
            </Grid>
          </Grid>
          <Typography variant="h6" sx={{ mt: 3 }}>
            Summa: €{totalPrice.toFixed(2)}
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCheckoutOpen(false)}>Tühista</Button>
          <Button 
            variant="contained" 
            onClick={handleCheckout}
            disabled={!formData.firstName || !formData.lastName || !formData.email || !formData.phone}
          >
            Kinnita makse
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert 
          onClose={() => setSnackbar({ ...snackbar, open: false })} 
          severity={snackbar.severity}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default ShopPage;
