import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Paper, Grid, CircularProgress } from '@mui/material';
import axios from 'axios';

// OpenWeather API configuration
const WEATHER_API_KEY = process.env.REACT_APP_WEATHER_API_KEY;
const WEATHER_API_URL = 'https://api.openweathermap.org/data/2.5/weather';

function WeatherPage() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!city) {
      setError('Please enter a city name.');
      return;
    }

    if (!WEATHER_API_KEY) {
      setError('Weather API key is not configured. Please check your .env file for REACT_APP_WEATHER_API_KEY.');
      return;
    }

    setLoading(true);
    setError('');
    setWeatherData(null);

    try {
      const response = await axios.get(WEATHER_API_URL, {
        params: {
          q: city,
          units: 'metric',
          appid: WEATHER_API_KEY
        }
      });

      setWeatherData(response.data);
    } catch (err) {
      console.error('Error fetching weather data:', err);
      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('Failed to fetch weather data. Please try again.');
      }
      setWeatherData(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Weather Information
      </Typography>

      <Box sx={{ mb: 4, display: 'flex', gap: 2, alignItems: 'center' }}>
        <TextField
          label="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              handleSearch();
            }
          }}
          disabled={loading}
        />
        <Button 
          variant="contained" 
          onClick={handleSearch}
          disabled={loading}
        >
          {loading ? <CircularProgress size={24} color="inherit" /> : 'Search'}
        </Button>
      </Box>

      {error && (
        <Paper elevation={3} sx={{ p: 2, mb: 2, bgcolor: '#fff3f3' }}>
          <Typography color="error">
            {error}
          </Typography>
        </Paper>
      )}

      {weatherData && (
        <Paper elevation={3} sx={{ p: 3, maxWidth: 400 }}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography variant="h5" gutterBottom>
                {weatherData.name}
              </Typography>
            </Grid>
            <Grid item xs={12} sx={{ textAlign: 'center' }}>
              <img
                src={`https://openweathermap.org/img/w/${weatherData.weather[0].icon}.png`}
                alt="Weather Icon"
                style={{ width: 100, height: 100 }}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography variant="h6">
                Temperature: {weatherData.main.temp}°C
              </Typography>
              <Typography>
                Description: {weatherData.weather[0].description}
              </Typography>
              <Typography>
                Humidity: {weatherData.main.humidity}%
              </Typography>
              <Typography>
                Wind Speed: {weatherData.wind.speed} m/s
              </Typography>
            </Grid>
          </Grid>
        </Paper>
      )}
    </Box>
  );
}

export default WeatherPage;
